# ### Removing Outliers

# In[25]:


# Box plot visualization for checking outliers:
df.boxplot(figsize=(10,4))
plt.show()


# In[26]:


Q1 = df.Weekly_Sales.quantile(0.25)
Q3 = df.Weekly_Sales.quantile(0.75)
IQR = Q3-Q1
df = df[(df.Weekly_Sales >= Q1-1.5*IQR) & (df.Weekly_Sales<=Q3+1.5*IQR)]


# In[32]:


Q1 = df.Unemployment.quantile(0.25)
Q3 = df.Unemployment.quantile(0.75)
IQR = Q3-Q1
df = df[(df.Unemployment >= Q1-1.5*IQR) & (df.Unemployment<=Q3+1.5*IQR)]


# In[31]:


Q1 = df.Temperature.quantile(0.25)
Q3 = df.Temperature.quantile(0.75)
IQR = Q3-Q1
df = df[(df.Temperature >= Q1-1.5*IQR) & (df.Temperature<=Q3+1.5*IQR)]


# In[29]:


Q1 = df.Holiday_Flag.quantile(0.25)
Q3 = df.Holiday_Flag.quantile(0.75)
IQR = Q3-Q1
df = df[(df.Holiday_Flag >= Q1-1.5*IQR) & (df.Holiday_Flag<=Q3+1.5*IQR)]


# In[33]:


# Box plot visualization for checking outliers:
df.boxplot(figsize=(10,4))
