# In[34]:


df.shape


# In[35]:


df_dummies = pd.get_dummies(df,columns=['Store','Holiday_Flag'])
print(df_dummies.columns.to_list())


# In[36]:


x = df_dummies.drop(['Weekly_Sales'], axis=1)
y = df_dummies['Weekly_Sales']


# In[37]:


x.head(5)


# In[38]:


y.head(5)


# In[39]:


#split data into train & test
x_train,x_test,y_train,y_test = train_test_split(x,y,test_size =0.2,random_state=6)


# ### **Linear Regression**

# In[40]:


lr = LinearRegression()
lr.fit(x_train, y_train)
LinearRegression()

#predictions
y_pred = lr.predict(x_test)
#sactter plot
plt.scatter(y_test,y_pred)

print('Linear Regression:')
# Print training and testing scores
print("Training dataset Score:", lr.score(x_train, y_train))
print("Testing dataset Score:", lr.score(x_test, y_test))
# Calculate mean squared error and root mean squared error
print("Mean Squared Error (MSE):", mean_squared_error(y_test, y_pred))
print("Root Mean Squared Error (RMSE):", np.sqrt(mean_squared_error(y_test, y_pred)))
# Calculate R^2 score
r2_lr=r2_score(y_test, y_pred)
print('R2 Score:',r2_lr)
#calculate adjusted r2 square
n = x_train.shape[0]  # Number of samples in training data
p = x_train.shape[1]  # Number of features
adj_r2 = 1 - (1 - r2_lr) * (n - 1) / (n - p - 1)
print("Adjusted R^2 Score:", adj_r2)


# ### **XGB Regressor**

# In[41]:


xg = XGBRegressor(n_estimators=100, random_state=42, enable_categorical=True)
xg.fit(x_train, y_train)

#predictions
y_pred = xg.predict(x_test)
#sactter plot
plt.scatter(y_test,y_pred)

print('XGBoost Regressor:')
# Print training and testing scores
print("Training dataset Score:", xg.score(x_train, y_train))
print("Testing dataset Score:", xg.score(x_test, y_test))
# Calculate mean squared error and root mean squared error
print("Mean Squared Error (MSE):", mean_squared_error(y_test, y_pred))
print("Root Mean Squared Error (RMSE):", np.sqrt(mean_squared_error(y_test, y_pred)))
# Calculate R^2 score
r2_xgb=r2_score(y_test, y_pred)
print('R2 Score:',r2_xgb)
#calculate adjusted r2 square
n = x_train.shape[0]  # Number of samples in training data
p = x_train.shape[1]  # Number of features
adj_r2 = 1 - (1 - r2_xgb) * (n - 1) / (n - p - 1)
print("Adjusted R^2 Score:", adj_r2)


# ### **Random Forest**

# In[42]:


rf = RandomForestRegressor(n_estimators = 100)
rf.fit(x_train, y_train)

#predictions
y_pred = rf.predict(x_test)
#sactter plot
plt.scatter(y_test,y_pred)

# Print training and testing scores
print("Training dataset Score:", rf.score(x_train, y_train))
print("Testing dataset Score:", rf.score(x_test, y_test))
# Calculate mean squared error and root mean squared error
print("Mean Squared Error (MSE):", mean_squared_error(y_test, y_pred))
print("Root Mean Squared Error (RMSE):", np.sqrt(mean_squared_error(y_test, y_pred)))
# Calculate R^2 score
r2_fr=r2_score(y_test, y_pred)
print("R^2 Score:", r2_fr)
# Calculate adjusted R-squared
n = x_train.shape[0]  # Number of samples in training data
p = x_train.shape[1]  # Number of features
adj_r2 = 1 - (1 - r2_fr) * (n - 1) / (n - p - 1)
print("Adjusted R^2 Score:", adj_r2)


# In[43]:


compare = {'model':['LinearRegression','RandomForestRegressor','XGBRegressor'],
           'R2':[r2_lr,r2_xgb,r2_fr]}
compare_df = pd.DataFrame(compare)
compare_df


# In[44]:


plt.figure(figsize=(7,5))
sns.barplot(x='model',y='R2',data=compare_df)
plt.show()


